import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game1/game1.css";
import game1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-1.png";
import { useState, useEffect } from "react";

export default function Game1() {
  const [count, setCount] = useState(0);
  const [zoom, setZoom] = useState(false);

  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer function */
  const padTime = time => {
    return String(time).length === 1 ? `0${time}` : `${time}`;
  };

  const format = time => {
    // Convert seconds into minutes and take the whole part
    const minutes = Math.floor(time / 60);

    // Get the seconds left after converting minutes
    const seconds = time % 60;

    //Return combined values as string in format mm:ss
    return `${minutes}:${padTime(seconds)}`;
  };

  useEffect(() => {
  const intervalId = setInterval(() => {
    setCount(prevCount => prevCount + 1);
  }, 1000);

  return () => clearInterval(intervalId);
}, []);

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters"></div>
        <div className="zoom counter">{count}</div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img src={game1} alt="game 1" />
      </div>

      {/* footer */}
      <div className="sec3"></div>
    </div>
  );
}
