import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/homepage.css";
import background from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/main-background.jpg";
import { useEffect, useState } from "react";

export default function HomePage() {

  const [isFullscreen, setIsFullscreen] = useState(false);

// Watch for fullscreenchange
useEffect(() => {
  function onFullscreenChange() {
    setIsFullscreen(Boolean(document.fullscreenElement));
  }

  document.addEventListener('fullscreenchange', onFullscreenChange);

  return () => document.removeEventListener('fullscreenchange', onFullscreenChange);
}, []);

  return (
    <div>
      <img className="background-image" src={background} alt="background" />
    </div>
  );
}
