import "/home/marv/the-odin-project/react-projects/anime-search/src/Leaderboard/leaderboard.css";
import {initializeApp} from "firebase/app";
import { Firestore,getFirestore, collection, getDocs } from "firebase/firestore"
import { useState, useEffect } from "react";

export default function LeaderBoard(props) {
    /* Firebase config */
    const firebaseConfig = {
      apiKey: "AIzaSyCpJa2ER-oPndvTFNv3GQt1RxiUmWdtaBs",
      authDomain: "anime-search-b3a3e.firebaseapp.com",
      projectId: "anime-search-b3a3e",
      storageBucket: "anime-search-b3a3e.appspot.com",
      messagingSenderId: "108541758225",
      appId: "1:108541758225:web:eb814a8ae862c688b9dab0"
    };

  // init firebase app
  initializeApp(firebaseConfig);

  //init services
  const db = getFirestore()

  //leaders ref
  const colRef = collection(db, 'leaders')

  //get leader data
  getDocs(colRef)
    .then((snapshot) => {
      let leaders = []
      snapshot.docs.forEach((doc) => {
        setData({ ...doc.data(), id: doc.id})
        leaders.push({ ...doc.data(), id: doc.id})
      })
      console.log(leaders)
    })
    .catch(err => {
      console.log(err.message)
    })

  const [data, setData] = useState([]);

  useEffect(() => {
    setData();
  }, [])

  return (
    <div>
      {
        data && data.map(datas=>{
          return(
            <div className="leaderboard-wrapper">
          <div className="leaderboard">
          <h1>LEADERBOARD</h1>
          <div>{data}</div>
        </div>
      </div>
          )
        })
      }
    </div>
  )
    }
