import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/homepage.css";
import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/NavBar/navbar.css";
import background from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/main-background.jpg";
import NavBar from "./NavBar/NavBar";
import StartScreen from "./StartScreen/StartScreen";
import { useState, useEffect } from "react";

export default function HomePage() {
  const [load, setLoad] = useState(true)

  useEffect(() => {
    const timeId = setTimeout(() => {
      // After 3 seconds set the show value to false
      setLoad(false)
    }, 8000)

    return () => {
      clearTimeout(timeId)
    }
  }, []);

  return (
    <div>
      {load ? <StartScreen />:null }
      <div className="navbar">
        <NavBar />
      </div>
      <div>
        <img className="background-image" src={background} alt="background" />
      </div>
    </div>
  );
}
