import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game1/game1.css";
import game1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-1.png";
import { useState, useEffect } from "react";

export default function Game1() {
  const [count, setCount] = useState(0);
  const [zoom, setZoom] = useState(false);

  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer function */
  useEffect(() => {
  const intervalId = setInterval(() => {
    setCount(prevCount => prevCount + 1);
  }, 1000);

  return () => clearInterval(intervalId);
}, []);

const makeTimeReadable = (d) => {
  d = Number(d);
  var h = Math.floor(d / 3600);
  var m = Math.floor(d % 3600 / 60);
  var s = Math.floor(d % 3600 % 60);

  var hDisplay = h > 0 ? h + (h === 1 ? " hour, " : " hours, ") : "";
  var mDisplay = m > 0 ? m + (m === 1 ? " minute, " : " minutes, ") : "";
  var sDisplay = s > 0 ? s + (s === 1 ? " second" : " seconds") : "";
  /* return hDisplay + mDisplay + sDisplay; */
  return `${checkTwoDigits(hDisplay)}:${checkTwoDigits(mDisplay)}:${checkTwoDigits(
    sDisplay
  )}`;
};

const checkTwoDigits = (t) => {
  if (t < 10) {
    return "0" + t;
  }
  return t;
};

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters"></div>
        <div className="zoom counter">{makeTimeReadable(count)}</div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img src={game1} alt="game 1" />
      </div>

      {/* footer */}
      <div className="sec3"></div>
    </div>
  );
}
