import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game1/game1.css";
import game1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-1.png";
import { useState, useEffect } from "react";

export default function Game1() {
  const [count, setCount] = useState(3600);
  const [zoom, setZoom] = useState(false);

  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer function */
  const [time, setTime] = useState(36000);

  useEffect(() => {
    const timer = setInterval(() => {
      setTime((prevTime) => prevTime + 1000);
    }, 1000);
    return () => {
      clearInterval(timer);
    };
  }, []);

  const makeTimeReadable = (t) => {
    const timeInSec = t / 1000;
    console.log(timeInSec);
    const hours = Math.floor(timeInSec / 3600);
    const mins = Math.floor((timeInSec - hours * 3600) / 60);

    const secs = timeInSec - hours * 3600 - mins * 60;
    console.log(hours, mins, secs, "mins");
    return `${checkTwoDigits(hours)}:${checkTwoDigits(mins)}:${checkTwoDigits(
      secs
    )}`;
  };

  const checkTwoDigits = (t) => {
    if (t < 10) {
      return "0" + t;
    }
    return t;
  };

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters"></div>
        <div className="zoom counter">{makeTimeReadable(time)}</div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img src={game1} alt="game 1" />
      </div>

      {/* footer */}
      <div className="sec3"></div>
    </div>
  );
}
