import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game2/game2.css";
import g2 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g2.png";
import { useState, useEffect } from "react";
import bart from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/bart.png";
import shrek from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/shrek.png";
import bowser from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/bowser.png";
import { Link } from "react-router-dom";
import CharacterMenu2 from "../CharacterMenu/CharacterMenu2";
import DetailsPopup from "../../DetailsPopup.js/DetailsPopup";
import db from "../../Firebase";
import { collection, getDocs, orderBy, query, addDoc } from "firebase/firestore";
import LeaderBoard from "../../Leaderboard/LeaderBoard";

export default function Game2() {
  const [seconds, setSeconds] = useState(0);
  const [zoom, setZoom] = useState(false);
  const [x, setX] = useState([]);
  const [y, setY] = useState([]);
  const [charMenu, setCharMenu] = useState(false);
  const [correctChar1, setCorrectChar1] = useState(false);
  const [correctChar2, setCorrectChar2] = useState(false);
  const [correctChar3, setCorrectChar3] = useState(false);
  const [bar, setBar] = useState([]);
  const [bows, setBows] = useState([]);
  const [shrk, setShrk] = useState([]);
  const [pop, setPop] = useState(false);
  const [one, setOne] = useState([]);
  const [two, setTwo] = useState([]);
  const [three, setThree] = useState([]);
  const [paused, setPaused] = useState(true);
  const [board, setBoard] = useState(false);
  const [name, setName] = useState('');

  /* firebase state */
  const [lists2, setLists2] = useState([]);

  /* firebase useEffect */
  useEffect(() => {
    getLeader2(db);
  }, []);

  /* firebase function */
  function getLeader2(db) {
    const getLeaders = query(collection(db, "game2"), orderBy("seconds"));
    getDocs(getLeaders)
      .then((response) => {
        const leaderList = response.docs.map((doc) => ({
          data: doc.data(),
          id: doc.id,
        }));
        setLists2(leaderList);
      })
      .catch((error) => console.log(error.message));
  }

  /* zoom handler */
  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer */
  useEffect(() => {
    if (paused) {
      const interval = setInterval(() => {
        setSeconds((seconds) => seconds + 1);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [paused]);

  /* timer formatter */
  function formatter(t) {
    const d = Number(t);
    /* const h = Math.floor(d / 3600); */
    const m = Math.floor((d % 3600) / 60);
    const s = Math.floor((d % 3600) % 60);
    /* const hDisplay = h > 0 ? `${h.toString().length > 1 ? `${h}` : `${0}${h}`}` : '00'; */
    const mDisplay =
      m > 0 ? `${m.toString().length > 1 ? `${m}` : `${m}`}` : "0";
    const sDisplay =
      s > 0 ? `${s.toString().length > 1 ? `${s}` : `${0}${s}`}` : "00";
    return `${mDisplay}:${sDisplay}`;
  }

  /* character menu */
  function displayCharacterMenu() {
    setCharMenu(true);
  }

  /* image click functions */
  function clickClick(e) {
    cods(e);
    displayCharacterMenu();
  }

  /* function for mapping mouse click coordinates  */
  function cods(e) {
    let posx = 0;
    let posy = 0;
    let TotalX = document.body.scrollWidth;
    let TotalY = document.body.scrollHeight;
    if (e.pageX || e.pageY) {
      posx = e.pageX;
      posy = e.pageY;
    } else if (e.clientX || e.clientY) {
      posx =
        e.clientX +
        document.body.scrollLeft +
        document.documentElement.scrollLeft;
      posy =
        e.clientY +
        document.body.scrollTop +
        document.documentElement.scrollTop;
    }
    /* console.log("X: " + (posx * 100) / TotalX);
    console.log("Y: " + (posy * 100) / TotalY); */

    setX((posx * 100) / TotalX);
    setY((posy * 100) / TotalY);
  }

  /* coords (left, right, top, bottom) */
  useEffect(() => {
    /* bart */
    if (
      (x >= 88 && x <= 89 && y >= 21.8 && y <= 25.7) ||
      (x >= 88.8 && x <= 89.7 && y >= 19.29 && y <= 23.58)
    ) {
      console.log("bart");
      setBar(["bart"]);
    }
    /* bowser */
    if (
      (x >= 76.12 && x <= 79.43 && y >= 77.67 && y <= 90.34) ||
      (x >= 76.93 && x <= 80.31 && y >= 88.42 && y <= 92.36)
    ) {
      console.log("bowser");
      setBows(["bowser"]);
    }
    /* shrek */
    if (
      (x >= 10.42 && x <= 12.11 && y >= 22.88 && y <= 25.7) ||
      (x >= 9.54 && x <= 11.21 && y >= 20.66 && y <= 23.92)
    ) {
      console.log("shrek");
      setShrk(["shrek"]);
    }
  }, [x, y]);

  /* menu selection correct or not */
  function clicker1() {
    if (bar[0] === "bart") {
      setOne(["one"]);
      setCorrectChar1(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  function clicker2() {
    if (bows[0] === "bowser") {
      setTwo(["two"]);
      setCorrectChar2(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  function clicker3() {
    if (shrk[0] === "shrek") {
      setThree(["three"]);
      setCorrectChar3(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  /* enter details menu appears */
  useEffect(() => {
    if (one[0] === "one" && two[0] === "two" && three[0] === "three") {
      setPop(true);
      setPaused(false);
    }
  }, [one, two, three]);



  /* details component */
  function handleSubmit(e){
    e.preventDefault()
    if(name === ''){
      return
    }
    const getLeaders = collection(db, "game1");
    addDoc(getLeaders, {seconds, name}).then(response => {
      console.log(response.id)
    }).catch(error => {
      console.log(error.message)
    })
    getLeader2(db);
    setPop(false);
    setBoard(true);
  }

  /* details component */
  function changes(e){
    setName(e.target.value)
  }

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters c2">
          <img src={bart} alt="bart" title="Bart" />
          <div className={correctChar1 ? "shade1-2" : "characters"}></div>
          <img className="bowser" src={bowser} alt="bowser" title="Bowser" />
          <div className={correctChar2 ? "shade2-2" : "characters"}></div>
          <img src={shrek} alt="shrek" title="Shrek" />
          <div className={correctChar3 ? "shade3-2" : "characters"}></div>
        </div>
        <div className="zoom counter">
          {formatter(seconds)}
          <Link to="/">
            <div className="home">
              <button>Home</button>
            </div>
          </Link>
        </div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img onClick={clickClick} src={g2} alt="game 1" />
      </div>
      {/* character popup menu */}
      {charMenu ? (
        <CharacterMenu2
          clicker1={clicker1}
          clicker2={clicker2}
          clicker3={clicker3}
          bart={bart}
          bowser={bowser}
          shrek={shrek}
        />
      ) : null}
      {pop ? <DetailsPopup changes={changes} name={name} handleSubmit={handleSubmit} sent={sent} seconds={seconds}  /> : null}
      {board ? <LeaderBoard lists={lists2}/>:null}
    </div>
  );
}
