import { initializeApp } from 'firebase/app';
import { getFirestore, collection, getDocs } from 'firebase/firestore/lite';

const firebaseConfig = {
  apiKey: "AIzaSyCpJa2ER-oPndvTFNv3GQt1RxiUmWdtaBs",
  authDomain: "anime-search-b3a3e.firebaseapp.com",
  projectId: "anime-search-b3a3e",
  storageBucket: "anime-search-b3a3e.appspot.com",
  messagingSenderId: "108541758225",
  appId: "1:108541758225:web:eb814a8ae862c688b9dab0"
};

const app = initializeApp(firebaseConfig);
export const db = getFirestore(app);

async function getCities(db) {
  const citiesCol = collection(db, 'leaders');
  const citySnapshot = await getDocs(citiesCol);
  const cityList = citySnapshot.docs.map(doc => doc.data());
  return cityList;
}

export default getCities;
