import "/home/marv/the-odin-project/react-projects/anime-search/src/DetailsPopup.js/detailsPopup.css";
import React, { useState } from "react";
import db from "../Firebase";
import { collection, addDoc } from "firebase/firestore";

export default function DetailsPopup(props) {

  const [name, setName] = useState('');

  const { timer } = props;

function handleSubmit(e){
  e.preventDefault()
  if(name === ''){
    return
  }
  const getLeaders = collection(db, "leaders");
  addDoc(getLeaders, {timer, name}).then(response => {
    console.log(response.id)
  }).catch(error => {
    console.log(error.message)
  })
}

  return (
    <div className="dis">
      <div className="details-popup">
        <h1>Your Time:</h1>
        <form onSubmit={handleSubmit}>
        <p id='time' type="text" value={timer}>{timer}</p>
        <label htmlFor="name">Name</label>
        <input id='name' type="text" value={name} required="required" onChange={e => setName(e.target.value)} />
        <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
}
