import g3 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g3.png";
import { useState, useEffect } from "react";
import luigi from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/luigi.png";
import simba from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/simba.png";
import ariel from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/ariel.png";
import { Link } from "react-router-dom";
import CharacterMenu3 from "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/CharacterMenu/CharacterMenu3.js";
import DetailsPopup from "../../DetailsPopup.js/DetailsPopup";
import {
  collection,
  getDocs,
  orderBy,
  query,
  addDoc,
} from "firebase/firestore";
import LeaderBoard from "../../Leaderboard/LeaderBoard";
import db from "../../Firebase";

export default function Game3() {
  const [seconds, setSeconds] = useState(0);
  const [zoom, setZoom] = useState(false);
  const [x, setX] = useState([]);
  const [y, setY] = useState([]);
  const [charMenu, setCharMenu] = useState(false);
  const [correctChar1, setCorrectChar1] = useState(false);
  const [correctChar2, setCorrectChar2] = useState(false);
  const [correctChar3, setCorrectChar3] = useState(false);
  const [lui, setLui] = useState([]);
  const [ari, setAri] = useState([]);
  const [sim, setSim] = useState([]);
  const [pop, setPop] = useState(false);
  const [one, setOne] = useState([]);
  const [two, setTwo] = useState([]);
  const [three, setThree] = useState([]);
  const [paused, setPaused] = useState(true);
  const [board, setBoard] = useState(false);
  const [name, setName] = useState("");

  /* firebase state */
  const [lists3, setLists3] = useState([]);

  /* firebase useEffect */
  useEffect(() => {
    getLeader3(db);
  }, []);

  /* firebase function */
  function getLeader3(db) {
    const getLeaders = query(collection(db, "game3"), orderBy("seconds"));
    getDocs(getLeaders)
      .then((response) => {
        const leaderList = response.docs.map((doc) => ({
          data: doc.data(),
          id: doc.id,
        }));
        setLists3(leaderList);
      })
      .catch((error) => console.log(error.message));
  }

  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer */
  useEffect(() => {
    if (paused) {
      const interval = setInterval(() => {
        setSeconds((seconds) => seconds + 1);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [paused]);

  /* timer formatter */
  function formatter(t) {
    const d = Number(t);
    /* const h = Math.floor(d / 3600); */
    const m = Math.floor((d % 3600) / 60);
    const s = Math.floor((d % 3600) % 60);
    /* const hDisplay = h > 0 ? `${h.toString().length > 1 ? `${h}` : `${0}${h}`}` : '00'; */
    const mDisplay =
      m > 0 ? `${m.toString().length > 1 ? `${m}` : `${m}`}` : "0";
    const sDisplay =
      s > 0 ? `${s.toString().length > 1 ? `${s}` : `${0}${s}`}` : "00";
    return `${mDisplay}:${sDisplay}`;
  }

  function displayCharacterMenu() {
    setCharMenu(true);
  }

  /* image click functions */
  function clickClick(e) {
    cods(e);
    displayCharacterMenu();
  }

  /* function for mapping mouse click coordinates  */
  function cods(e) {
    let posx = 0;
    let posy = 0;
    let TotalX = document.body.scrollWidth;
    let TotalY = document.body.scrollHeight;
    if (e.pageX || e.pageY) {
      posx = e.pageX;
      posy = e.pageY;
    } else if (e.clientX || e.clientY) {
      posx =
        e.clientX +
        document.body.scrollLeft +
        document.documentElement.scrollLeft;
      posy =
        e.clientY +
        document.body.scrollTop +
        document.documentElement.scrollTop;
    }
    /* console.log("X: " + (posx * 100) / TotalX);
    console.log("Y: " + (posy * 100) / TotalY); */

    setX((posx * 100) / TotalX);
    setY((posy * 100) / TotalY);
  }

  /* coords (left, right, top, bottom) */
  useEffect(() => {
    /* luigi */
    if (
      (x >= 12.32 && x <= 13.09 && y >= 79.5 && y <= 81.67) ||
      (x >= 11.48 && x <= 12.12 && y >= 80.7 && y <= 83.3)
    ) {
      console.log("luigi");
      setLui(["lui"]);
    }
    /* ariel */
    if (
      (x >= 76.33 && x <= 78.38 && y >= 88.17 && y <= 90.56) ||
      (x >= 77.3 && x <= 79 && y >= 89.87 && y <= 92.45)
    ) {
      console.log("ariel");
      setAri(["ari"]);
    }
    /* simba */
    if (
      (x >= 67.46 && x <= 69.7 && y >= 20.71 && y <= 22.66) ||
      (x >= 67.97 && x <= 70.22 && y >= 18.09 && y <= 20.66)
    ) {
      console.log("simba");
      setSim(["sim"]);
    }
  }, [x, y]);

  /* menu selection correct or not */
  function clicker1() {
    if (lui[0] === "lui") {
      setOne(["one"]);
      setCorrectChar1(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  function clicker2() {
    if (ari[0] === "ari") {
      setTwo(["two"]);
      setCorrectChar2(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  function clicker3() {
    if (sim[0] === "sim") {
      setThree(["three"]);
      setCorrectChar3(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  /* enter details menu appears */
  useEffect(() => {
    if (one[0] === "one" && two[0] === "two" && three[0] === "three") {
      setPop(true);
      setPaused(false);
    }
  }, [one, two, three]);

  /* details component */
  function handleSubmit(e) {
    e.preventDefault();
    if (name === "") {
      return;
    }
    const getLeaders = collection(db, "game3");
    addDoc(getLeaders, { seconds, name })
      .then((response) => {
        console.log(response.id);
      })
      .catch((error) => {
        console.log(error.message);
      });
    getLeader3(db);
    setPop(false);
  }

  /* details component */
  function changes(e) {
    setName(e.target.value);
  }

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters">
          <img src={luigi} alt="luigi" title="Luigi" />
          <div className={correctChar1 ? "shade1-3" : "characters"}></div>
          <img src={ariel} alt="ariel" title="Ariel" />
          <div className={correctChar2 ? "shade2-3" : "characters"}></div>
          <img className="sim" src={simba} alt="simba" title="Simba" />
          <div className={correctChar3 ? "shade3-3" : "characters"}></div>
        </div>
        <div className="zoom counter">
          {formatter(seconds)}
          <Link to="/">
            <div className="home">
              <button>Home</button>
            </div>
          </Link>
        </div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img onClick={clickClick} src={g3} alt="game 1" />
      </div>
      {/* character popup menu */}
      {charMenu ? (
        <CharacterMenu3
          clicker1={clicker1}
          clicker2={clicker2}
          clicker3={clicker3}
          luigi={luigi}
          ariel={ariel}
          simba={simba}
        />
      ) : null}
      {pop ? (
        <DetailsPopup
          changes={changes}
          name={name}
          handleSubmit={handleSubmit}
          seconds={seconds}
        />
      ) : null}
      {board ? <LeaderBoard /> : null}
    </div>
  );
}
