import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game1/game1.css";
import game1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-1.png";
import { useState, useEffect } from "react";

export default function Game1() {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [zoom, setZoom] = useState(false);

  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer function */

  function toggle(){
    setIsActive(!isActive);
  }

  function reset(){
    setSeconds(0);
    setIsActive(false);
  }

  useEffect(() => {
    let interval = null;
    if (isActive){
      interval = setInterval(() => {
        setSeconds(seconds => seconds +1);
      }, 1000);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters"></div>
        <div className="zoom counter">{seconds}s</div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img src={game1} alt="game 1" />
      </div>

      {/* footer */}
      <div className="sec3"></div>
    </div>
  );
}
