import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game1/game1.css";
import g1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g1.jpg";
import { useState,useEffect, useRef } from "react";

export default function Game1() {
  const [seconds, setSeconds] = useState(0);
  const [zoom, setZoom] = useState(false);
  const pos = useRef();

  function mouseCoords(e){
    var a, x = 0, y = 0;
    e = e || window.event;
    /* Get the x and y positions of the image: */
    a = img.getBoundingClientRect();
    /* Calculate the cursor's x and y coordinates, relative to the image: */
    x = e.pageX - a.left;
    y = e.pageY - a.top;
    /* Consider any page scrolling: */
    x = x - window.pageXOffset;
    y = y - window.pageYOffset;
    return {x : x, y : y};
    console.log(e.clientX)
  }

  /* zoom button click */
  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer */
  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds(seconds => seconds + 1);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  /* timer formatter */
  function formatter (t){
    const d = Number(t);
    /* const h = Math.floor(d / 3600); */
    const m = Math.floor((d % 3600) / 60);
    const s = Math.floor((d % 3600) % 60);
    /* const hDisplay = h > 0 ? `${h.toString().length > 1 ? `${h}` : `${0}${h}`}` : '00'; */
    const mDisplay = m > 0 ? `${m.toString().length > 1 ? `${m}` : `${m}`}` : '0';
    const sDisplay = s > 0 ? `${s.toString().length > 1 ? `${s}` : `${0}${s}`}` : '00';
    return `${mDisplay}:${sDisplay}`;
  };

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters"></div>
        <div className="zoom counter">{formatter(seconds)}</div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in" : "sec2"}>
        <img ref={pos} onClick={mouseCoords} src={g1} alt="game 1" />
      </div>

      {/* footer */}
      <div className="sec3"></div>
    </div>
  );
}
