import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/StartScreen/startScreen.css";

export default function StartScreen() {

   /* state for loading screen */
   const [load, setLoad] = useState(true);

   /* unmount loading screen */
   useEffect(() => {
     setTimeout(() => {
       setLoad(false);
     }, 4000);
   }, []);

  return (
    <div className="fadein">
      {load ? <img className="start-screen" src={startscreen} alt="start screen" /> :null}
    </div>
  );
}
