import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game1/main-game.css";
import g1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g1.jpg";
import { useState, useEffect, useCallback, useMemo } from "react";
import mario from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/mario.png";
import pikachu from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/pikachu.png";
import bandicoot from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/bandicoot.png";
import { Link } from "react-router-dom";
import CharacterMenu from "./characterMenu";
import DetailsPopup from "../../DetailsPopup.js/DetailsPopup";

export default function Game1() {
  const [seconds, setSeconds] = useState(0);
  const [zoom, setZoom] = useState(false);
  const [x, setX] = useState([]);
  const [y, setY] = useState([]);
  const [charMenu, setCharMenu] = useState(false);
  const [correctChar1, setCorrectChar1] = useState(false);
  const [correctChar2, setCorrectChar2] = useState(false);
  const [correctChar3, setCorrectChar3] = useState(false);
  const [pika, setPika] = useState([]);
  const [bandi, setBandi] = useState([]);
  const [mar, setMar] = useState([]);
  const [pop, setPop] = useState(false);
  const [displayBoard, setDisplayBoard] = useState([]);

  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer */
  useEffect(() => {
    const interval = setInterval(() => {
      setSeconds((seconds) => seconds + 1);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  /* timer formatter */
  function formatter(t) {
    const d = Number(t);
    /* const h = Math.floor(d / 3600); */
    const m = Math.floor((d % 3600) / 60);
    const s = Math.floor((d % 3600) % 60);
    /* const hDisplay = h > 0 ? `${h.toString().length > 1 ? `${h}` : `${0}${h}`}` : '00'; */
    const mDisplay =
      m > 0 ? `${m.toString().length > 1 ? `${m}` : `${m}`}` : "0";
    const sDisplay =
      s > 0 ? `${s.toString().length > 1 ? `${s}` : `${0}${s}`}` : "00";
    return `${mDisplay}:${sDisplay}`;
  }

  function displayCharacterMenu() {
    setCharMenu(true);
  }

  /* image click functions */
  function clickClick(e) {
    cods(e);
    displayCharacterMenu();
  }

  /* function for mapping mouse click coordinates  */
  function cods(e) {
    let posx = 0;
    let posy = 0;
    let TotalX = document.body.scrollWidth;
    let TotalY = document.body.scrollHeight;
    if (e.pageX || e.pageY) {
      posx = e.pageX;
      posy = e.pageY;
    } else if (e.clientX || e.clientY) {
      posx =
        e.clientX +
        document.body.scrollLeft +
        document.documentElement.scrollLeft;
      posy =
        e.clientY +
        document.body.scrollTop +
        document.documentElement.scrollTop;
    }
    console.log("X: " + (posx * 100) / TotalX);
    console.log("Y: " + (posy * 100) / TotalY);

    setX((posx * 100) / TotalX);
    setY((posy * 100) / TotalY);
  }

  useEffect(() => {
    Mario(x, y);
    Bandicoot(x, y);
    Pikachu(x, y);
  }, [Mario, Bandicoot, Pikachu]);

  /* coords (left, right, top, bottom) */
const Mario = useCallback((x,y) => {
  if (
    (x >= 6 && x <= 11 && y >= 67 && y <= 74) ||
    (x >= 0.05 && x <= 3.4 && y >= 67.67 && y <= 76.2)
  ) {
    console.log(mar);
    setMar(["mar"])
  }
}, [])

const Bandicoot = useCallback((x,y) => {
  if (
    (x >= 20.3 && x <= 22.3 && y >= 42.8 && y <= 46.2) ||
    (x >= 16 && x <= 18.39 && y >= 42.3 && y <= 45)
  ) {
    //console.log(bandi);
    setBandi(["bandi"])
  }
}, [])

const Pikachu = useCallback((x,y) => {
  if (
    (x >= 74.7 && x <= 77.8 && y >= 71.14 && y <= 76.24) ||
    (x >= 77.49 && x <= 80.75 && y >= 72.2 && y <= 77.95)
  ) {
    // console.log(pika);
    setPika(["pikachu"]);
  }
}, []) 


  /* menu selection correct or not */
function clicker1(){
  if(mar[0] === "mar"){
    setCorrectChar1(true);
    setDisplayBoard(['one']);
    setCharMenu(false);
  }else{
    setCharMenu(false);
  }
}

function clicker2(){
  if(bandi[0] === "bandi"){
    setCorrectChar2(true);
    setDisplayBoard(['two']);
    setCharMenu(false)
  }else{
    setCharMenu(false);
  }
}

  function clicker3(){
    if(pika[0] === "pikachu"){
      setCorrectChar3(true);
      setDisplayBoard(['three']);
      setCharMenu(false)
    }else{
      setCharMenu(false);
    }
  }

  /* leaderboard display */
  const leaderBoard = useCallback(() =>{
    if(displayBoard.length === 1){
      setPop(true)
    }
  },[displayBoard])

  useEffect(() => {
    leaderBoard()
  },[leaderBoard])

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters">
          <img className="rot" src={mario} alt="mario" title="Mario" />
          <div className={correctChar1 ? "shade1" : "characters"}></div>
          <img src={bandicoot} alt="crash bandicoot" title="Crash Bandicoot" />
          <div className={correctChar2 ? "shade2" : "characters"}></div>
          <img src={pikachu} alt="naruto" title="Pikachu" />
          <div className={correctChar3 ? "shade3" : "characters"}></div>
        </div>
        {/* timer and home button */}
        <div className="zoom counter">
          {formatter(seconds)}
          <Link to="/">
            <div className="home">
              <button>Home</button>
            </div>
          </Link>
        </div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in img" : "sec2"}>
        <img onClick={clickClick} src={g1} alt="game 1" />
      </div>
      {/* character popup menu */}
      {charMenu ? (
        <CharacterMenu clicker1={clicker1} clicker2={clicker2} clicker3={clicker3} mario={mario} bandicoot={bandicoot} pikachu={pikachu} />
      ) : null}
  {pop ? (
    <DetailsPopup/>
  ): null}
    </div>
  );
}
