import { Link } from "react-router-dom";
import background from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/main-background.jpg";
import g1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g1.jpg";
import g2 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g2.png";
import g3 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g3.png";
import g4 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g4.jpeg";
import cup from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/podium.png";
import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/GameSelection/gameselection.css";
import LeaderBoard from "../../Leaderboard/LeaderBoard";
import React, { useRef, useEffect } from "react";
import { useState } from "react";

/* Firebase imports */
import {initializeApp} from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore"

export default function GameSelection() {

  /* Firebase config */
  const firebaseConfig = {
    apiKey: "AIzaSyCpJa2ER-oPndvTFNv3GQt1RxiUmWdtaBs",
    authDomain: "anime-search-b3a3e.firebaseapp.com",
    projectId: "anime-search-b3a3e",
    storageBucket: "anime-search-b3a3e.appspot.com",
    messagingSenderId: "108541758225",
    appId: "1:108541758225:web:eb814a8ae862c688b9dab0"
  };

// init firebase app
initializeApp(firebaseConfig);

//init services
const db = getFirestore()

//leaders ref
const colRef = collection(db, 'leaders')

//get leader data
getDocs(colRef)
  .then((snapshot) => {
    let leaders = []
    snapshot.docs.forEach((doc) => {
      setData({ ...doc.data(), id: doc.id})
      leaders.push({ ...doc.data(), id: doc.id})
    })
    console.log(leaders)
  })
  .catch(err => {
    console.log(err.message)
  })

  useEffect(() => {
    getDocs();
  }, [])

/* state and Ref */
const [data, setData] = useState([])
  const [show, setShow] = useState(false);
  const wrapperRef = useRef(null);
  useOutsideAlerter(wrapperRef);

  /* show leaderboard */
  function board() {
    setShow(true);
  }

  /* hide leaderboard */
  function useOutsideAlerter(ref) {
    useEffect(() => {
      function handleClickOutside(event) {
        if (ref.current && !ref.current.contains(event.target)) {
          setShow(false);
        }
      }
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref]);
  }

  return (
    <div className="wrapper">
      <img className="background-image" src={background} alt="background" />

      <div className="game-selection">
        {/* navbar */}
        <div className="nav">
          <div className="full-width"></div>
          <div className="full-width title-text">
            <div className="p1">
              <span className="a">A</span>
              <span className="n">N</span>
              <span className="i">I</span>
              <span className="m">M</span>
              <span className="e">E</span>
            </div>
            <div className="p2">
              <span className="ss">S</span>
              <span className="ee">E</span>
              <span className="aa">A</span>
              <span className="rr">R</span>
              <span className="cc">C</span>
              <span className="hh">H</span>
            </div>
          </div>
          <div className="full-width cup">
            <img
              onClick={board}
              title="Leader Board"
              className="up"
              src={cup}
              alt="cup"
            />
          </div>
        </div>

        {/* games selection */}
        <div className="wrapper2">
          <div>
            <p>Game 1</p>
            <Link to="game1">
              <img src={g1} alt="game1" />
            </Link>
          </div>
          <div>
            <p>Game 2</p>
            <Link to="game2">
              <img src={g2} alt="game1" />
            </Link>
          </div>
          <div>
            <p>Game 3</p>
            <Link to="game3">
              <img src={g3} alt="game1" />
            </Link>
          </div>
          <div>
            <p>Game 4</p>
            <Link to="game4">
              <img src={g4} alt="game1" />
            </Link>
          </div>
        </div>
      </div>
      {show ? (
        <div ref={wrapperRef}>
          <LeaderBoard data={data}/>
        </div>
      ) : null}
    </div>
  );
}
