import "/home/marv/the-odin-project/react-projects/anime-search/src/GameLinks/Game4/game4.css";
import g4 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/g4.jpeg";
import { useState, useEffect } from "react";
import gow from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/gow.png";
import zelda from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/zelda.png";
import magneto from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/Characters/magneto.png";
import { Link } from "react-router-dom";
import CharacterMenu4 from "../CharacterMenu/CharacterMenu4";
import DetailsPopup from "../../DetailsPopup.js/DetailsPopup";
import {
  collection,
  getDocs,
  orderBy,
  query,
  addDoc,
} from "firebase/firestore";
import LeaderBoard from "../../Leaderboard/LeaderBoard";
import db from "../../Firebase";

export default function Game4() {
  const [seconds, setSeconds] = useState(0);
  const [zoom, setZoom] = useState(false);
  const [x, setX] = useState([]);
  const [y, setY] = useState([]);
  const [charMenu, setCharMenu] = useState(false);
  const [correctChar1, setCorrectChar1] = useState(false);
  const [correctChar2, setCorrectChar2] = useState(false);
  const [correctChar3, setCorrectChar3] = useState(false);
  const [kra, setKra] = useState([]);
  const [mag, setMag] = useState([]);
  const [zel, setZel] = useState([]);
  const [pop, setPop] = useState(false);
  const [one, setOne] = useState([]);
  const [two, setTwo] = useState([]);
  const [three, setThree] = useState([]);
  const [paused, setPaused] = useState(true);
  const [board, setBoard] = useState(false);
  const [name, setName] = useState("");

  /* firebase state */
  const [lists, setLists] = useState([]);
  const [lists2, setLists2] = useState([]);
  const [lists3, setLists3] = useState([]);
  const [lists4, setLists4] = useState([]);

  /* firebase useEffect */
  useEffect(() => {
    getLeader4(db);
  }, []);

  /* firebase function */
  function getLeader4(db) {
    const getLeaders = query(collection(db, "game4"), orderBy("seconds"));
    getDocs(getLeaders)
      .then((response) => {
        const leaderList = response.docs.map((doc) => ({
          data: doc.data(),
          id: doc.id,
        }));
        setLists4(leaderList);
      })
      .catch((error) => console.log(error.message));
  }


  const handleClick = (event) => {
    setZoom((current) => !current);
  };

  /* timer */
  useEffect(() => {
    if (paused) {
      const interval = setInterval(() => {
        setSeconds((seconds) => seconds + 1);
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [paused]);

  /* timer formatter */
  function formatter(t) {
    const d = Number(t);
    /* const h = Math.floor(d / 3600); */
    const m = Math.floor((d % 3600) / 60);
    const s = Math.floor((d % 3600) % 60);
    /* const hDisplay = h > 0 ? `${h.toString().length > 1 ? `${h}` : `${0}${h}`}` : '00'; */
    const mDisplay =
      m > 0 ? `${m.toString().length > 1 ? `${m}` : `${m}`}` : "0";
    const sDisplay =
      s > 0 ? `${s.toString().length > 1 ? `${s}` : `${0}${s}`}` : "00";
    return `${mDisplay}:${sDisplay}`;
  }

  function displayCharacterMenu() {
    setCharMenu(true);
  }

  /* image click functions */
  function clickClick(e) {
    cods(e);
    displayCharacterMenu();
  }

  /* function for mapping mouse click coordinates  */
  function cods(e) {
    let posx = 0;
    let posy = 0;
    let TotalX = document.body.scrollWidth;
    let TotalY = document.body.scrollHeight;
    if (e.pageX || e.pageY) {
      posx = e.pageX;
      posy = e.pageY;
    } else if (e.clientX || e.clientY) {
      posx =
        e.clientX +
        document.body.scrollLeft +
        document.documentElement.scrollLeft;
      posy =
        e.clientY +
        document.body.scrollTop +
        document.documentElement.scrollTop;
    }
    /* console.log("X: " + (posx * 100) / TotalX);
    console.log("Y: " + (posy * 100) / TotalY); */

    setX((posx * 100) / TotalX);
    setY((posy * 100) / TotalY);
  }

  /* coords (left, right, top, bottom) */
  useEffect(() => {
    /* kratos */
    if (
      (x >= 48.8 && x <= 54.29 && y >= 66.34 && y <= 71.88) ||
      (x >= 48.86 && x <= 54.07 && y >= 66.64 && y <= 72.67)
    ) {
      console.log("kratos");
      setKra(["kra"]);
    }
    /* magneto */
    if (
      (x >= 35.14 && x <= 38 && y >= 60.5 && y <= 63.22) ||
      (x >= 35.1 && x <= 38.4 && y >= 60.45 && y <= 63.47)
    ) {
      console.log("magneto");
      setMag(["mag"]);
    }
    /* zelda */
    if (
      (x >= 66.9 && x <= 70.84 && y >= 65.17 && y <= 70.23) ||
      (x >= 66.49 && x <= 70.51 && y >= 64.49 && y <= 70.79)
    ) {
      console.log("zelda");
      setZel(["zel"]);
    }
  }, [x, y]);

  /* menu selection correct or not */
  function clicker1() {
    if (kra[0] === "kra") {
      setOne(["one"]);
      setCorrectChar1(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  function clicker2() {
    if (mag[0] === "mag") {
      setTwo(["two"]);
      setCorrectChar2(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  function clicker3() {
    if (zel[0] === "zel") {
      setThree(["three"]);
      setCorrectChar3(true);
      setCharMenu(false);
    } else {
      setCharMenu(false);
    }
  }

  /* enter details menu appears */
  useEffect(() => {
    if (one[0] === "one" && two[0] === "two" && three[0] === "three") {
      setPop(true);
      setPaused(false);
    }
  }, [one, two, three]);

  /* details component */
  function handleSubmit(e) {
    e.preventDefault();
    if (name === "") {
      return;
    }
    const getLeaders = collection(db, "game4");
    addDoc(getLeaders, { seconds, name })
      .then((response) => {
        console.log(response.id);
      })
      .catch((error) => {
        console.log(error.message);
      });
    getLeader4(db);
    setPop(false);
  }

  /* details component */
  function changes(e) {
    setName(e.target.value);
  }

  return (
    <div className="main-wrap1">
      {/* nav bar */}
      <div className="sec1">
        <div className="zoom characters">
          <img src={gow} alt="kratos" title="Kratos" />
          <div className={correctChar1 ? "shade1-4" : "characters"}></div>
          <img src={magneto} alt="magneto" title="Magneto" />
          <div className={correctChar2 ? "shade2-4" : "characters"}></div>
          <img src={zelda} alt="zelda" title="Zelda" />
          <div className={correctChar3 ? "shade3-4" : "characters"}></div>
        </div>
        <div className="zoom counter">
          {formatter(seconds)}
          <Link to="/">
            <div className="home">
              <button>Home</button>
            </div>
          </Link>
        </div>

        <div className="zoom zooms">
          <button className="hov" title="Zoom in & out" onClick={handleClick}>
            Zoom
          </button>
        </div>
      </div>

      {/* search area */}
      <div className={zoom ? "zoom-in4 img" : "sec2"}>
        <img onClick={clickClick} src={g4} alt="game 1" />
      </div>
      {/* character popup menu */}
      {charMenu ? (
        <CharacterMenu4
          clicker1={clicker1}
          clicker2={clicker2}
          clicker3={clicker3}
          gow={gow}
          magneto={magneto}
          zelda={zelda}
        />
      ) : null}
      {pop ? (
        <DetailsPopup
          changes={changes}
          name={name}
          handleSubmit={handleSubmit}
          seconds={seconds}
        />
      ) : null}
      {board ? <LeaderBoard /> : null}
    </div>
  );
}
