import { useState, useEffect } from "react";
/* css imports */
import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/homepage.css";
import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/GameSelection/gameselection.css";
import "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/StartScreen/startScreen.css";
/* image imports */
import background from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/main-background.jpg";
import game1 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-1.png";
import game2 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-2.png";
import game3 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-3.png";
import game4 from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/game-4.jpg";
import cup from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/podium.png";
import startScreen from "/home/marv/the-odin-project/react-projects/anime-search/src/HomePage/Images/loading.jpg";
/* components */
import GameSelection from "./GameSelection/GameSelection";
import StartScreen from "./StartScreen/StartScreen";

export default function HomePage() {
  /* state for loading screen */
  const [load, setLoad] = useState(true);

  /* unmount loading screen */
  useEffect(() => {
    setTimeout(() => {
      setLoad(false);
    }, 4000);
  }, []);

  return (
    <div>
      {/* background */}
      <div>
        <img className="background-image" src={background} alt="background" />
      </div>

      {/* loading sreen */}
      {load ? <StartScreen startScreen={startScreen}/> : null}

      {/* game selection */}
      <GameSelection
        cup={cup}
        game1={game1}
        game2={game2}
        game3={game3}
        game4={game4}
      />
    </div>
  );
}
